import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'
import {calculator} from './calculator';
import {operators} from './operators';
import {operands} from './operands';

class DisplayPanel extends React.Component{


    render(){
        return <div className="display-panel">
            <div id="val-display">{this.props.value}</div>
            </div>
    }

}

const CalcButton = (props) => {

    return (
         <button className="btn btn-primary" onClick = {() => {props.btnClick(calculator[props.value])}} >{calculator[props.value]}</button>
    );
}

class Calculator extends React.Component{

    constructor(props){
        super(props);

        this.state = {

            OperandsStack:[],
            OperatorStack:[],
            isOp : false,
            result : ''
        }

    }


     collectOperands = (value) =>{

        let op = this.state.result;
        op = op+value;

        this.setState({

            result:op
        })

    }

    Calculate = (NumStack,OpStack) => {

        let num2index = 1;


        for (let i=0; i<OpStack.length; i++){

            if(!NumStack[num2index]){
            let num1 = parseFloat(NumStack[0]);
            let num2 = parseFloat(NumStack[num2index]);
            const op = OpStack[i];

            switch(op){

                case "+": num1 = num1+num2;
                           break;
                case "-": num1 = num1-num2;
                          break;
                case "*": num1 = num1*num2;
                          break;
                case "/": num1 = num1/num2;
                          break;
                default : break;
            }

            NumStack[num2index]=num1;
            NumStack.splice(1,1);
            num2index++;
        } 

    }

    this.setState({
        result : NumStack[0]
    })

    }


    btnClick = (value) => {

        console.log(value);

        if(operands.includes(value)){

             this.collectOperands(value);   

        }

         if(operators.includes(value)){

            const res = this.state.result;
            let operandsStack = this.state.OperandsStack.slice();
            let operatorStack = this.state.OperatorStack.slice();
            if(value === "="){

                this.Calculate(this.state.OperandsStack,this.state.OperatorStack);
            }
            else{
            this.setState({
                OperandsStack: operandsStack.concat(res),
                OperatorStack: operatorStack.concat(value),
                result : ''
            }) }
        }

        }

    render(){

            console.log(this.state.OperatorStack);

        return(<div className="container calc">


                <DisplayPanel value ={this.state.result}/>
                <div className="calc-body">

                    <div className="calc-row">
                    <CalcButton value={0} btnClick ={() => this.btnClick(calculator[0])}/>
                    <CalcButton value={1} btnClick ={() => this.btnClick(calculator[1])}/>
                    <CalcButton value={2} btnClick ={() => this.btnClick(calculator[2])}/>
                    <CalcButton value={3} btnClick ={() => this.btnClick(calculator[3])}/>

                    </div>

                    <div className="calc-row">

                    <CalcButton value={4} btnClick ={() => this.btnClick(calculator[4])}/>
                    <CalcButton value={5} btnClick ={() => this.btnClick(calculator[5])}/>
                    <CalcButton value={6} btnClick ={() => this.btnClick(calculator[6])}/>
                    <CalcButton value={7} btnClick ={() => this.btnClick(calculator[7])}/>

                    </div>

                    <div className="calc-row">
                    <CalcButton value={8} btnClick ={() => this.btnClick(calculator[8])}/>
                    <CalcButton value={9} btnClick ={() => this.btnClick(calculator[9])}/>
                    <CalcButton value={10} btnClick ={() => this.btnClick(calculator[10])}/>
                    <CalcButton value={11} btnClick ={() => this.btnClick(calculator[11])}/>

                    </div>



                    <div className="calc-row">
                    <CalcButton value={12} btnClick ={() => this.btnClick(calculator[12])}/>
                    <CalcButton value={13} btnClick ={() => this.btnClick(calculator[13])}/>
                    <CalcButton value={14} btnClick ={() => this.btnClick(calculator[14])}/>
                    <CalcButton value={15} btnClick ={() => this.btnClick(calculator[15])}/>
                    <CalcButton value={16} btnClick ={() => this.btnClick(calculator[16])}/>

                    </div>

                </div>

              </div>);
    }
}



ReactDOM.render(

    <Calculator /> , document.getElementById("root")
);
