import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'

class DisplayPanel extends React.Component{


    render(){
        return <div className="display-panel">{this.props.value}</div>
    }

}

class Calculator extends React.Component{

    constructor(props){
        super(props);

        this.state = {

            Operand : '',
            Operator : ''
        }
       const calculator = ["AC","7","8","9","/",
                           "4","5","6","*",
                           "1","2","3","-",
                           ".","0","+"] 
    }


    btnClick = (value) => {

        this.setState({Operand:'', Operator:''})
        let op = this.state.Operand;
        op = op + value;
        this.setState({Operand: op})


    }

    render(){

        return(<div className="container calc">

                <div className="display">
                <DisplayPanel value ={this.state.Operand}/>
                </div>
                <div className="calc-body">

                    <div className="calc-row">
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("9")}} >9</button>
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("8")}} >8</button>
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("7")}} >7</button>
                    </div>

                    <div className="calc-row">
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("6")}} >6</button>
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("5")}} >5</button>
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("4")}} >4</button>
                    </div>

                    <div className="calc-row">
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("3")}} >3</button>
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("2")}} >2</button>
                    <button className="btn btn-primary" onClick = {() => {this.btnClick("1")}} >1</button>
                    </div>

                </div>

              </div>);
    }
}



ReactDOM.render(

    <Calculator /> , document.getElementById("root")
);
