import React from 'react';
import ReactDOM from 'react-dom';
import './index.css'
import {calculator} from './constants';

class DisplayPanel extends React.Component{


    render(){
        return <div className="display-panel">{this.props.value}</div>
    }

}

const CalcButton = (props) => {

    for(let i=0;i<props.calcarray.length;i++){

        return (
            <button className="btn btn-primary" onClick = {() => {this.btnClick(calculator[props.value])}} >{calculator[props.value]}</button>
       );
    }

}

class Calculator extends React.Component{

    constructor(props){
        super(props);

        this.state = {

            Operand : '',
            Operator : ''
        }

    }


    btnClick = (value) => {

        let op = this.state.Operand;
        op = op + value;
        this.setState({Operand: op})


    }

    render(){

        return(<div className="container calc">

                <DisplayPanel value ={this.state.Operand}/>
                <div className="calc-body">

                <CalcButton calcarray={calculator}/>   

                </div>

              </div>);
    }
}



ReactDOM.render(

    <Calculator /> , document.getElementById("root")
);
